function global:Where-DbaObject {
    
    [CmdletBinding()]
    param (
        [Parameter(ValueFromPipeline, Mandatory)]
        [object]
        $InputObject,

        [Parameter(Position = 0)]
        [Alias('Property')]
        [string]
        $PropertyName,

